import styled from 'styled-components'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const Label = styled.label`
  display: block;
  font-weight: 700;
  padding: 10px;
  min-height: 40px;
  cursor: pointer;
`;

const FormGroup = styled.div`
  position: relative;
  z-index: 1
`;

const Input = styled.input`
  width: 100%;
  /* background: #FFFFFF; */
  border-radius: 3px;
  height: 45px;
  line-height: 45px;
  padding: 0 40px 0 10px;
  border: 2px solid gray;

  &:focus {
    border: 3px solid #0075FF;
    outline: none;
    box-shadow: 3px 0px 30px rgba(163, 163 163 0.4);
  }
`;

const MessageError = styled.p`
  font-size: 12px;
  margin-bottom: 0;
  color: red;
  display: none;
`;

const IconCheck = styled(FontAwesomeIcon)`
  position: absolute;
  right: 10px;
  bottom: 14px;
  z-index: 2;
  font-size: 16px;
  opacity: 1;
`;

const Button = styled.button`
  width: 100%;
  border: none;
  border-radius: 3px;
  height: 45px;
  line-height: 45px;
  background: blue;
  color: #FFFFFF;
  font-weight: bold;
  cursor: pointer;

  &:hover {
    background: green;
  }
`;


export {
  Label,
  FormGroup,
  Input,
  MessageError,
  IconCheck,
  Button
}
